//puttting the data in local strorage
if (localStorage.getItem("pro") == null) {
  localStorage.setItem("pro", JSON.stringify(data));
}
// making html structure for cards
function appendpro() {
  let data = JSON.parse(localStorage.getItem("pro"));
  let mainDiv = document.getElementById("products");
  data.forEach(function (el) {
    //target this
    let div = document.createElement("div");
    div.setAttribute("class", "classname")

    //product name
    let p_name = document.createElement("p");
    p_name.innerHTML = el.name;

    //product price
    let p_Price = document.createElement("p");
    p_Price.innerHTML =` ${100-(p_Price.innerHTML % Math.floor(Math.random() * 10))} `
    //console.log(p_Price.innerHTML/2)

    let discount = document.createElement("p");
    discount.innerHTML = `<del>${el.price}</del>`

    //meta data
    let meta = document.createElement("p");
    meta.innerHTML = el.metadata;
    meta.style.fontWeight = "lighter";

    //button
    let btn = document.createElement("button");
    btn.textContent = "Add to cart";
    btn.addEventListener("click", function () {
      addToCart(el);
    });
    let img = document.createElement("img");
    img.src = el.img;
    div.append(img, p_name, meta, discount, p_Price, btn);
    mainDiv.append(div);
    img.addEventListener("click", function () {
      window.location.href = "producdetail.html";
    });
  });
}
appendpro();
//add to cart method
function addToCart(obj) {
  let cart = [];
  cart = localStorage.getItem("cart");
  if (cart == null) {
    cart = [];
  } else {
    cart = JSON.parse(localStorage.getItem("cart"));
  }
  cart.push(obj);
  localStorage.setItem("cart", JSON.stringify(cart));
}
